def allLabels():
	labels = [c1+v+c2 for c1 in C1 for v in V for c2 in C2 if c1 != c2]
	f = open('dictionary.txt')
	text = f.read(); f.close()
	english_words = list(set([line.split(':')[0] for line in text.split('\n')])) + exclusions
	return [label for label in labels if label not in english_words and pops[label] < 70000000]

